// Find matching subject key (case-insensitive)
function findSubjectKey(subject) {
    if (!subject) return null;
    const keys = Object.keys(QUESTION_BANK);
    return keys.find(k => k.toLowerCase() === subject.toLowerCase()) || null;
}

// Shuffle array helper
function shuffleArray(array) {
    const shuffled = [...array];
    for (let i = shuffled.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
}

/**
 * Get subject-specific questions from the built-in bank
 * @param {number} count - Number of questions needed
 * @param {string} subject - Subject name
 * @returns {Array} Formatted questions
 */
function getQuestionsFromBank(count, subject) {
    const key = findSubjectKey(subject);
    const subjectQuestions = key ? QUESTION_BANK[key] : null;
    if (!subjectQuestions || subjectQuestions.length === 0) {
        return [];
    }

    // Shuffle and pick the requested count
    const shuffled = shuffleArray(subjectQuestions);
    const selected = shuffled.slice(0, Math.min(count, shuffled.length));

    // Format each question for the app
    return selected.map(q => ({
        questionText: q.questionText,
        questionType: 'multiple-choice',
        options: shuffleArray(q.options),
        correctAnswer: q.correctAnswer,
        difficulty: q.difficulty || 'medium',
        difficultyScore: q.difficulty === 'easy' ? 3 : q.difficulty === 'medium' ? 5 : 8,
        expectedTime: q.difficulty === 'easy' ? 30 : q.difficulty === 'medium' ? 60 : 90,
        tags: [subject],
        explanation: `${subject} programming question`,
        hints: [`This is a ${q.difficulty} level ${subject} question`],
        status: 'active',
        source: 'built-in',
        statistics: { totalAttempts: 0, correctAttempts: 0, averageTime: 0 }
    }));
}

/**
 * Check if a subject has questions in the built-in bank
 */
function hasSubjectInBank(subject) {
    const key = findSubjectKey(subject);
    return !!(key && QUESTION_BANK[key] && QUESTION_BANK[key].length > 0);
}

/**
 * Get list of all available subjects in the bank
 */
function getAvailableSubjects() {
    return Object.keys(QUESTION_BANK);
}

module.exports = {
    getQuestionsFromBank,
    hasSubjectInBank,
    getAvailableSubjects,
    QUESTION_BANK
};
